    border-width: $ch5-slider-handle-border-width;
    background: $ch5-slider-handle-bg;
    box-shadow: none;
  }

  /* ===============================================
    STRECH
   ============================================= */
  &.ch5-slider--strech--width.ch5-slider--orientation--horizontal{
    width: 100% !important;
  }

  &.ch5-slider--strech--height.ch5-slider--orientation--vertical{
    height: 100% !important;
  }


  &.ch5-slider--strech--height.ch5-slider--orientation--horizontal {
    height: 100% !important;
    .noUi-target {
      top: 50%;
      transform: translateY(-50%);
    }
  }

  &.ch5-slider--strech--width.ch5-slider--orientation--vertical {
    width: 100% !important;
    .noUi-target {
      left: 50%;
      transform: translateX(-50%);
    }
  }

  &.ch5-slider--strech--both.ch5-slider--orientation--horizontal{
    width: 100% !important;
